import './App.css';
import React, {useState, button} from 'react';

function App() {
  const [weightInputText, setWeightText] = useState('');
  const [repsInputText, setRepsText] = useState('');

  const handleWeightChange = weight => {
    setWeightText(weight.value);
  }

  const handleRepsChange = reps => {
    setRepsText(reps.value);
  }

  const handleCalculateClick = () => {
    // Handle button click logic here
    const oneRepMax = weightInputText / ((1.0278) - (0.0278 * repsInputText));
    console.log('Button clicked!');
  };

  

  return (
    <div className="App">
      <h1>hi</h1>
      <label>
        Weight:
        <input
          type="text"
          value={weightInputText}
          onChange={handleWeightChange}
        />
      </label>

      <label>
        Reps:
        <input
          type="text"
          value={repsInputText}
          onChange={handleRepsChange}
        />
      </label>

      <button onClick={handleCalculateClick}>Calculate</button>
      <label>
        1 Rep Max:
        <input
          type="text"
          value={oneRepMax}
          readOnly
        />
      </label>
    </div>
  );
}

export default App;
